// CHECK-NEXT:    [[TMP2:%.*]] = load i8 addrspace(200)*, i8 addrspace(200)* addrspace(200)* getelementptr inbounds ([[STRUCT_ANON]], [[STRUCT_ANON]] addrspace(200)* @c, i64 0, i32 1), align 16
